import { useState } from 'react';
import { signInWithEmailAndPassword } from "firebase/auth";
import Button from "../button/button.component.jsx";
import FormInput from "../form-input/form-input.component.jsx";
import { auth } from "../../utils/firebase/firebase.utils.js";

const defaultFormFields={
    email: '',
    password: '',
}

const SignInForm=()=>{
    const [formFields, setFormFields]=useState(defaultFormFields);
    const { email, password }=formFields;

    const handleSubmit=async(event)=>{
        event.preventDefault();
        try {
            const signedInUser=await signInWithEmailAndPassword(auth, email, password);
            const user=signedInUser.user;
            console.log(user);
        }catch(error) {
            if(error.code=="auth/user-not-found") {
                console.error("Your account details are incorrect. Please try again");
            } else {
                console.error(error.message);
            };
        };
    };

    const handleChange=(event)=>{
        const { name, value }=event.target;
        setFormFields({ ...formFields, [name]:value })
    };

    
    return (
        <div className="sign-in-container">
            <h2>Already have an account?</h2>
            <span>Sign in with your email address here</span>
            <form onSubmit={ handleSubmit }>
                <FormInput
                    label="Email"
                    type="email"
                    name="email"
                    value={ email }
                    onChange={ handleChange }
                    required
                />
                <FormInput
                    label="Password"
                    type="password"
                    name="password"
                    value={ password }
                    onChange={ handleChange }
                    required
                />
                <Button type="submit">Sign In</Button>
            </form>
        </div>
    )
}

export default SignInForm;
