import { useContext } from 'react';
import { CartContext } from '../../contexts/cart.context.jsx';
import "./checkout-product.styles.scss";

const increaseQuantity=()=>{
    console.log("Increasing quantity");
 };
const decreaseQuantity=()=>{ alert("This is decrease") };
const removeProduct=()=>{ alert("This is removal") };

const CheckoutProduct=({ product })=>{
    const { id, name, price, imageUrl, quantity }=product;

    const { addItemToCart }=useContext(CartContext);

    return (
        <div key={ id } className="product-container">
            <div className="product-section">
                <img src={ imageUrl } alt={ `${ name }`}/>
            </div>
            <div className="product-section">
                <span>{ name }</span>
            </div>
            <div className="product-section">
                <div className="product-quantity">
                    <span onClick={ decreaseQuantity }>&#60;</span>
                    <span>{ quantity }</span>
                    <span onClick={ increaseQuan(product) }>&#62;</span>
                </div>
            </div>
            <div className="product-section">
                <span>{ price }</span>
            </div>
            <div className="product-section">
                <span className="product-removal" onClick={ ()=>removeProduct(id) }>X</span>
            </div>
        </div>
    )
};

export default CheckoutProduct;
