import { createContext, useState, useEffect } from 'react';

const addCartItem=(cartItems, productToAdd)=>{
    const existingCartItem=cartItems.find((cartItem)=>cartItem.id===productToAdd.id);
    if(existingCartItem){
        return cartItems.map((cartItem)=>cartItem.id===productToAdd.id?{ ...cartItem, quantity: cartItem.quantity+1 }:cartItem);
    }
    return [...cartItems, { ...productToAdd, quantity:1 }]
};

export const CartContext=createContext({
    isCartOpen: false,
    setIsCartOpen: ()=>{},
    cartItems: [],
    addItemToCart:()=>{},
    //Part of my cartQuantity:0,
    cartCount: 0,
});

export const CartProvider=({ children })=>{
    const [ isCartOpen, setIsCartOpen ]=useState(false);
    const [ cartItems, setCartItems ]=useState([
        {
        "id":5,
        "name":"Cessna Citation X",
        "imageUrl":"https://aeroaffaires.com/wp-content/uploads/2020/03/cessna_citation_x-768x527.jpg",
        "price":21500000,
        "quantity":1,
        }
    ]);
    const [ cartCount, setCartCount ]=useState(0);
    const addItemToCart=(productToAdd)=>{
        setCartItems(addCartItem(cartItems, productToAdd));
    }

    useEffect(()=>{
        const newCartCount=cartItems.reduce((total,cartItem)=>total+cartItem.quantity,0);
        setCartCount(newCartCount);
    },[cartItems]);


    /*
    MY SOLUTION
    const addItemToCart=(productToAdd)=>{
        const res=addCartItem(cartItems, productToAdd);
        setCartItems(res);
        setCartQuantity(()=>{
            return res.reduce((accumulator, currentElement)=>accumulator+currentElement.quantity,0)
        });
    };
    */
    const value={ isCartOpen, setIsCartOpen, addItemToCart, cartItems, setCartItems, cartCount };
    return <CartContext.Provider value={ value }>{ children }</CartContext.Provider>;
};
