import { useState, useContext } from 'react';
import Button from "../button/button.component.jsx";
import { UserContext } from "../../contexts/user.component.jsx";
import FormInput from "../form-input/form-input.component.jsx";
import { signInWithGooglePopup, createUserDocumentFromAuth, signInAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils.js";
import "./sign-in-form.styles.scss";

const defaultFormFields={
    email: '',
    password: '',
}

const SignInForm=()=>{
    const [formFields, setFormFields]=useState(defaultFormFields);
    const { email, password }=formFields;

    const { setCurrentUser }=useContext(UserContext);

    const resetFormFields=()=>{
        setFormFields(defaultFormFields);
    };

    const handleSubmit=async(event)=>{
        event.preventDefault();
        try {
            const { user }=await signInAuthUserWithEmailAndPassword(email, password);
            console.log("setCurrentUser",user);
            setCurrentUser(user);
            resetFormFields();
        }catch(error) {
            switch(error.code) {
                case 'auth/wrong-password':
                    alert("Incorrect password. Please try again");
                    break;
                case 'auth/user-not-found':
                    alert("Account not found");
                    break;
                default:
                    console.log(error);
            }
            console.log(error);
        };
    };

    const handleChange=(event)=>{
        const { name, value }=event.target;
        setFormFields({ ...formFields, [name]:value })
    };

    const signInWithGoogle=async()=>{
        const { user }=await signInWithGooglePopup();
        await setCurrentUser(user);
        await createUserDocumentFromAuth(user);
    };
    
    return (
        <div className="sign-in-container">
            <h2>Already have an account?</h2>
            <span>Sign in with your email address here</span>
            <form onSubmit={ handleSubmit }>
                <FormInput
                    label="Email"
                    type="email"
                    name="email"
                    value={ email }
                    onChange={ handleChange }
                    required
                />
                <FormInput
                    label="Password"
                    type="password"
                    name="password"
                    value={ password }
                    onChange={ handleChange }
                    required
                />
                <div className="buttons-container">
                    <Button type="submit">Sign In</Button>
                    <Button buttonType="google" type="button" onClick={ signInWithGoogle }>Google Signin</Button>
                </div>
            </form>
        </div>
    )
}

export default SignInForm;
