import { useState } from 'react';
import { createAuthUserWithEmailAndPassword, createUserDocumentFromAuth } from '../../utils/firebase/firebase.utils.js';

const defaultFormFields={
    displayName:'',
    email:'',
    password:'',
    confirmPassword:'',
};

const SignUpForm=()=>{
    const [formFields, setFormFields]=useState(defaultFormFields);
    const { displayName, email, password, confirmPassword }=formFields;

    const resetFormFields=()=>{
        setFormFields({ displayName:'Bruno' });
    };

    const handleSubmit=async(event)=>{
        event.preventDefault();
        if(password!=confirmPassword){
            alert("The passwords do not match");
            return;
        };
        try{
            const { user }=await createAuthUserWithEmailAndPassword(email, password);
            await createUserDocumentFromAuth(user, { displayName })
        }catch(error){
            if(error.code=="auth/email-already-in-use"){
                alert("Cannot create user, email already in use");
            }else{
                console.log("User creation encountered an error",error);
            }
        };
    };
    const handleChange=(event)=>{
        console.log(event.target);
        const { name, value}=event.target;
        console.log(name);
        console.log(value);
        setFormFields({...formFields, [name]:value});
    };
    return (
        <div>
            <h1>Sign up with your email address</h1>
            <form onSubmit={ handleSubmit }>
                {
                    //Include check for length of each input for added security
                }
                <label>Display Name</label> 
                <input type="text" name="displayName" value={ displayName } onChange={ handleChange } required/>
                <label>Email</label>
                <input type="email" name="email" value={ email } onChange={ handleChange } required/>
                <label>Password</label>
                <input type="password" name="password" value={ password } onChange={ handleChange } required/>
                <label>Confirm Password</label>
                <input type="password" name="confirmPassword" value={ confirmPassword } onChange={ handleChange } required/>
                <button type="submit">Sign up!</button>
            </form>
        </div>
    )
};

export default SignUpForm;
