import styled from "styled.components";

export const FormInputGroup=styled.div`
  $sub-color: grey;
  $main-color: black;

  @mixin shrinkLable {
    top: -14px;
    font-size: 12px;
    color: $main-color;
  }

  position: relative;
  margin: 45px 0;
`;

export const FormInput=styled.input`
  background
`



