import { useContext } from 'react';
import { CartContext, setCartItems } from "../../contexts/cart.context.jsx";
import "./checkout.styles.scss";

const Checkout=()=>{
    const { cartItems, setCartItems }=useContext(CartContext);
    const increaseQuantity=()=>{ alert("Increasing quantity");}
    const reduceQuantity=()=>{ alert("Decreasing quantity");}
    const removeMe=(itemId)=>{
        console.log(cartItems);
        alert("Removing item from cart");
        console.log("itemId",itemId);
        console.log("Find function",cartItems.find(item=>item.id===itemId));
        console.log("index",cartItems.indexOf(cartItems.find(item=>item.id===itemId)));
        console.log(cartItems.slice(cartItems.indexOf(cartItems.find(item=>item.id===itemId)),1));
        console.log(cartItems);
        setCartItems(cartItems);
        console.log(cartItems);
    };
    return (
        <div className="checkout-container">
            <div className="checkout-headings">
                <span className="checkout-heading">AIRCRAFT</span>
                <span className="checkout-heading">DESCRIPTION</span>
                <span className="checkout-heading">QUANTITY</span>
                <span className="checkout-heading">PRICE</span>
                <span className="checkout-heading">REMOVE</span>
            </div>
            <hr/>
            <div className="products-container">
                {
                    cartItems.map(({ id, name, imageUrl, price, quantity })=>
                        <div key={ id } className="product-container">
                            <div className="product-section">
                                <img src={ imageUrl } alt={ `${ name }`}/>
                            </div>
                            <div className="product-section">
                                <span>{ name }</span>
                            </div>
                            <div className="product-section">
                                <div className="product-quantity">
                                    <span onClick={ reduceQuantity }>&#60;</span>
                                    <span>{ quantity }</span>
                                    <span onClick={ increaseQuantity }>&#62;</span>
                                </div>
                            </div>
                            <div className="product-section">
                                <span>{ price }</span>
                            </div>
                            <div className="product-section">
                                <span className="product-removal" onClick={ ()=>removeMe({ id }) }>X</span>
                            </div>
                        </div>
                    )
                }
            </div>
        </div>
    )
};

export default Checkout;
