import { useState } from 'react';
import { createAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils.js";

const defaultFormFields={
    displayName:'',
    email:'',
    password:'',
    confirmPassword:'',
}

const SignUpForm=()=>{
    const [formFields, setFormFields]=useState(defaultFormFields);
    const { displayName, email, password, confirmPassword }=formFields;
    const handleChange=(event)=>{
        const { name, value }=event.target;
        setFormFields({...formFields, [name]:value});
    };
    return (
        <div>
            <h1>Sign up with your email address</h1>
            <form>
                <label>Display Name:</label>
                <input type="text" value={ displayName } onChange={handleChange} required/>
                <label>Email:</label>
                <input type="email" value={ email } onChange={handleChange} required/>
                <label>Password:</label>
                <input type="password" value={ password } onChange={handleChange} required/>
                <label>Confirm Password:</label>
                <input type="password" value={ confirmPassword } onChange={handleChange} required/>
                <button type="submit">Sign Up!</button>
            </form>
        </div>
    )
}

export default SignUp;
