import { useState } from 'react';
import { createAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils.js";

const handleSubmit=async(event)=>{
    event.preventDefault();
    const eventDisplayName=event.target[0].value;
    const eventEmail=event.target[1].value;
    const eventPassword=event.target[2].value;
    const eventConfirmPassword =event.target[3].value;
    if(!eventPassword==eventConfirmPassword) return;
    const newUser=await createAuthUserWithEmailAndPassword(eventEmail, eventPassword);
    console.log("newUser",newUser.userCredential.user);
    //event.target.submit();
};

const defaultFormFields={
    displayName:'',
    email:'',
    password:'',
    confirmPassword:'',
}

const SignUpForm=()=>{
    const [formFields, setFormFields]=useState(defaultFormFields);
    const { displayName, email, password, confirmPassword }=formFields;
    const handleChange=(event)=>{
        const { name, value }=event.target;
        setFormFields({...formFields, [name]:value});
    }
    return (
        <div>
            <h1>Sign up with your email address</h1>
            <form onSubmit={(event)=>{handleSubmit(event);}}>
                <label>Display Name:</label>
                <input type="text" name="displayName" value="Big Duck" onChange={ handleChange } />
                <label>Email:</label>
                <input type="email" name="email" value="bigdck@gmail.com" onChange={ handleChange } />
                <label>Password:</label>
                <input type="password" name="password" value="Ingrid" onChange={ handleChange } />
                <label>Confirm Password:</label> 
                <input type="password" name="confirmPassword" value="Ingrid" onChange={ handleChange } />
                <button type="submit">Sign Up</button>                
            </form>
        </div>
    )
}

export default SignUpForm;
