import { createContext, useState, useEffect } from 'react';

const addCartItem=(cartItems, productToAdd)=>{
    const existingCartItem=cartItems.find((cartItem)=>cartItem.id===productToAdd.id);
    if(existingCartItem) {
        return cartItems.map((cartItem)=>cartItem.id===productToAdd.id?{ ...cartItem, quantity: cartItem.quantity+1 }:cartItem);
    }
    return [ ...cartItems, { ...productToAdd, quantity:1 }]
};

const reduceItemQuantity=(cartItems, productToReduce)=>{
    if(productToReduce.quantity===1) {
        return cartItems.filter(item=>item!==productToReduce);
    };
    return cartItems.map((cartItem)=>cartItem.id===productToReduce.id?{ ...cartItem, quantity: cartItem.quantity-1 }:cartItem);
};

const removeItemFromCart=(cartItems, productToRemove)=>{
    cartItems.splice(cartItems.indexOf(cartItems.find(item=>item.id===productToRemove.id)),1);
    console.log("cartItems following splice are: ",typeof(cartItems));
    console.log("cartItems following splice are:",cartItems);
    return cartItems;
};

export const CartContext=createContext({
    isCartOpen: false,
    setIsCartOpen: ()=>{},
    cartItems: [],
    addItemToCart: ()=>{},
    cartCount: 0,
    totalPrice: 0,
});

export const CartProvider=({ children })=>{
    const [ isCartOpen, setIsCartOpen ]=useState(false);
    //const [cartItems, setCartItems ]=useState([]);
    const [cartItems, setCartItems ]=useState([]);
    const [ cartCount, setCartCount ]=useState(0);
    const [ totalPrice, setTotalPrice ]=useState(cartItems.reduce((total,item)=>total+item.price*item.quantity,0));

    const addItemToCart=(productToAdd)=>{
        setCartItems(addCartItem(cartItems, productToAdd));
    };

    const reduceCartItem=(productToReduce)=>{
        setCartItems(reduceItemQuantity(cartItems, productToReduce));
    };

    const removeCartItem=(productToRemove)=>{
        const res=removeItemFromCart(cartItems, productToRemove);
        setCartItems(res);
    };

    useEffect(()=>{
        setCartCount(cartItems.reduce((total,cartItem)=>total+cartItem.quantity,0));
    },[cartItems]);

    useEffect(()=>{
        setTotalPrice(cartItems.reduce((total,item)=>total+item.price*item.quantity,0));
    },[cartItems]);

    /*
    MY SOLUTION
    const addItemToCart=(productToAdd)=>{
        const res=addCartItem(cartItems, productToAdd);
        setCartItems(res);
        setCartQuantity(()=>{
            return res.reduce((accumulator, currentElement)=>accumulator+currentElement.quantity,0);
        });
    };
    */

    const value={ isCartOpen, setIsCartOpen, addItemToCart, reduceCartItem, removeCartItem, cartItems, setCartItems, cartCount, totalPrice};
    return <CartContext.Provider value={ value }>{ children }</CartContext.Provider>
};
